SELECT `cd_ventapieza` as id, `nu_preciocobrado` as precio, `nu_preciomin` as precio_minimo,`ds_apynomcliente` as cliente,`nu_docCliente` as documento,`ds_telcliente` as telefono, `ds_motocliente` as moto, ventapieza.`cd_sucursal` as sucursal_id, `nu_pedidoreparacion` as pedido, `dt_ventapieza` as fecha, `ds_descripcion` as descripcion, CASE `nu_destino`
                                                                                                                                                                                                                                                                                                                                                       WHEN '1' THEN 'Sal√≥n'
                                                                                                                                                                                                                                                                                                                                                       WHEN '2' THEN 'Sucursal'
                                                                                                                                                                                                                                                                                                                                                       WHEN '3' THEN 'Taller'
    END as destino, usuario.ds_nomusuario as user_name
FROM `ventapieza`
         LEFT JOIN usuario on ventapieza.cd_usuario = usuario.cd_usuario
